$(document).ready(function(){$(".header__burger").click(function(e){$(".header__burger,.header__menu").toggleClass("active"),$("body").toggleClass("lock")})});var swiper=new Swiper(".mySwiper",{pagination:{el:".swiper-pagination"}});SmoothScroll({animationTime:800,stepSize:75,accelerationDelta:30,accelerationMax:2,keyboardSupport:!0,arrowScroll:50,pulseAlgorithm:!0,pulseScale:4,pulseNormalize:1,touchpadSupport:!0});const anchors=document.querySelectorAll('a[href*="#"]');for(let t of anchors)t.addEventListener("click",function(e){e.preventDefault();e=t.getAttribute("href").substr(1);document.getElementById(e).scrollIntoView({behavior:"smooth",block:"start"})});